function colorz() {
	const {java, source} = this;
	let cb,cf;
	try {
		cb = source.getLoginInfoMap().get('封面背景颜色');
		if (cb=='') throw '空';
		} catch(err) {cb='151E27';}
	try {
		cf = source.getLoginInfoMap().get('封面字体颜色');
		if (cf=='') throw '空';
		} catch(err) {cf='dddddd';}
return [cb,cf];
}

function hexToInt(e, t) {
        var r = e.substr(t, 2);
        return parseInt(r, 16);
    }
function decodeEmail(n, c) {
    var o = "", a = hexToInt(n, c), i = c + 2;
    for (i; i < n.length; i += 2) {
        var l = hexToInt(n, i) ^ a;
        o += String.fromCharCode(l);
    }
        o = decodeURIComponent(escape(o));
        return o
}


