json=java.bytesToStr(result)
java.log(json)
img=JSON.parse(json)
result=java.hexDecodeToByteArray(java.ajax(`${img.pic},{"type": "jpg","headers": {"Referer": "https://hanmm.org/"}}`))
if (img.arr==""){
	result
}else{	arr=JSON.parse(java.base64Decode(img.arr))
	result=decodeImg(result,arr)
}
result

