bText=bText!=""?"回复:『"+bName+bText+"』":"";
lz=java.getString("@@tag.img@html").match(/ico_lz/)?"👤":"📜";
lz=(i==0 && lznum)?"👤":lz


j=org.jsoup.Jsoup.parse(list[i])
