var other_tags={"正剧":"27","悲剧":"908","喜剧":"909","古代":"562","民国":"639","现代":"563","未来":"71","HE":"29","BE":"315","OE":"560","小甜饼":"24","轻松":"28","治愈":"565","H/C":"536","爽文":"47","狗血":"4","致郁":"564","虐文":"576","惊悚":"566","悬疑":"85","雷文":"76","谐趣":"1151","文艺":"936","意识流":"981","浪漫主义":"983","现实主义":"982","魔幻现实":"1630","黑色幽默":"925","暗黑":"37","第一人称":"932","第二人称":"933","第三人称":"934","多重视角":"931","女主视角":"928","男主视角":"927","主受视角":"469","主攻视角":"468","仙侠":"77","反乌托邦":"1076","女尊":"1075","末世":"1074","原始社会":"1062","玄幻":"644","灵异":"643","科幻":"642","奇幻":"641","神话":"569","赛博朋克":"21","魔法世界":"63","武侠":"58","神怪志异":"42","童话":"36","星际":"35","架空世界":"33","幻想空间":"26","蒸汽朋克":"22","哨兵向导":"5","动物世界":"66","人鱼":"79","兽人":"80","拟人":"478","血族":"984","日系":"56","欧美":"230","韩流":"231","东南亚":"232","西亚":"233","澳洲":"234","非洲":"235","拉美":"236","西方":"640","极地":"722","网游":"62","直播":"1184","室友":"1063","战争":"1060","宫斗":"721","刑侦":"597","竞技体育":"161","电竞":"160","宅斗":"84","娱乐圈":"6","宫廷侯爵":"60","田园乡村":"59","商战":"55","校园":"54","职业文":"49","网配":"34","灵魂伴侣":"2250","传奇":"67","励志":"31","日常":"53","暧昧":"926","暗恋":"935","养成":"1186","青梅竹马":"44","宠物奇缘":"46","乔装改扮":"977","扮猪吃虎":"577","前世今生":"48","天作之合":"30","相爱相杀":"978","因缘邂逅":"43","阴差阳错":"32","破镜重圆":"2","PTSD":"1059","先婚后爱":"769","宗教":"1150","异能":"2255","克系":"2249","升级流":"2248","机甲":"2247","规则怪谈":"2236","推理":"1760","民俗":"1585","无限流":"1310","快穿":"1309","论坛体":"1185","性转":"20","公路文":"1064","灵魂转换":"1061","权谋":"980","单元剧":"770","基建":"61","美食":"45","重生":"41","穿越":"40","人工智能":"39","系统":"38","忠犬":"81","女王":"82","病娇":"906","自恋":"979","强强":"1","美强":"23","强弱":"25","弱强":"73","弱弱":"567","年上":"570","年下":"571","师生":"572","上下级":"573","下克上":"772","水仙":"1666","1v1":"3","互攻":"70","受受":"72","直掰弯":"78","受转攻":"574","攻转受":"575","无差":"719","受宠攻":"1072","攻宠受":"1073","全年龄":"720","清水":"86","布袋戏":"88","歌剧":"90","综漫":"479","Fate系列":"549","原作向":"715","paro":"716","AU":"717","全员向":"718","高达":"1325","授权翻译":"467","授权转载":"561","法语":"1077","德语":"1078","日语":"1079","西语":"1080","英语":"1081","粤语":"1631","第五期征集":"1668","第六期征集":"1836","第七期征集":"1988","第八期征集":"2109","第九期征集":"2286"}

// 解析 fenlei 变量
var fenleiItems = fenlei ? fenlei.split("，").map(function (item) { return item.trim(); }) : [];

// 获取 progress, length, orientation, recommendation, sorting, other_tags 的值
var values = {};
var otherTagValues = [];
for (var i = 0; i < fenleiItems.length; i++) {
    var item = fenleiItems[i];
    if (progress[item]) values.progress = progress[item];
    else if (length[item]) values.length = length[item];
    else if (orientation[item]) values.orientation = orientation[item];
    else if (recommendation[item]) values.recommendation = recommendation[item];
    else if (sorting[item]) values.sorting = sorting[item];
    else if (other_tags[item]) otherTagValues.push(other_tags[item]); // 支持 other_tags
}

// 生成 withTag 部分
var withTag = [values.progress, values.length, values.orientation, values.recommendation].concat(otherTagValues).filter(Boolean).join("-");

// 一行的个数
var defaultNumPerRow = 0;
var flexBasisMap = { 1: 1, 2: 0.4, 3: 0.25, 4: 0.2, 5: 0.15, 7: 0.1, 10: 0.05 };
var flexBasis = defaultNumPerRow === 0 ? undefined : flexBasisMap[defaultNumPerRow] || 1;

// 生成 style 对象
var style = { layout_flexGrow: 1 };
if (flexBasis !== undefined) {
    style.layout_flexBasisPercent = flexBasis;
}

// 生成结果数组
var result = [{"title":"我要签到","url":"/qiandao","style":{"layout_flexGrow":1}}];
if (fenlei) {
