        title: "当前筛选为【" + fenlei + "】",
        url: "",
        style: { layout_flexGrow: 1, layout_flexBasisPercent: 1 }
    });
}

for (var key in originality) {
    if (originality.hasOwnProperty(key)) {
        var inChannel = originality[key];
        var url = "/books?ordered=" + (values.sorting || "default") + "&withBianyuan=include_bianyuan&inChannel=" + inChannel + "&page={{page}}&withBianyuan=include_bianyuan";
        if (withTag) {
            url += "&withTag=" + withTag; // 添加 withTag 部分
        }
        result.push({
            title: key,
            url: url,
            style: { layout_flexGrow: 1, layout_flexBasisPercent: 1 }
        });
        for (var tagKey in other_tags) {
            if (other_tags.hasOwnProperty(tagKey)) {
                var tagValue = other_tags[tagKey];
                var finalWithTag = tagValue + (withTag ? "-" + withTag : "");
                result.push({
                    title: tagKey,
                    url: "/books?withTag=" + finalWithTag + "&page={{page}}&ordered=" + (values.sorting || "default") + "&withBianyuan=include_bianyuan&inChannel=" + inChannel,
                    style: style // 使用动态生成的 style 对象
                });
            }
        }
    }
}

// 收藏功能
收藏 = String(java.ajax("https://xn--pxtr7m.com")).match(/<a href="([^"]+)">收藏/);
if(收藏){
	收藏url = 收藏[1];
let sss=source.getVariable()||"";
收藏页 = sss==""? String(java.ajax(收藏url)):sss;
J = org.jsoup.Jsoup.parse(收藏页).select(".nav.nav-tabs");
source.setVariable(J)
J.select("li a").forEach(x=>{
	if(x.text()!=""&&x.text()!="全部标记已读"){
	      result.unshift({"title":x.text(),"url":x.attr("href")+"&page={{page}}","style":{"layout_flexGrow":1}})
	 }
	});
	result.unshift({"title":"刷新收藏列表","url":`{{if(page==1) {source.setVariable('');java.longToast("请❗️【刷新】❗️发现")};'http://127.0.0.1/'+page}}`,"style":{"layout_flexGrow":1,layout_flexBasisPercent: 1}})
}

JSON.stringify(result);

